    console.log(user);
  };

  getUser();

  return (
    <Nav>
      <ul>
        <li>
          <Link href="/">
            <a>Home</a>
          </Link>
        </li>
        <li>
          <Link href="/billing-info">
            <a>Billing Info</a>
          </Link>
        </li>
        <li>
          <Link href="/about">
            <a>About</a>
          </Link>
        </li>
        {!loading &&
          (user ? (
            <>
              <li>
                {/* You can use <Link /> here too */}
                <a href="/profile">Profile</a>
              </li>
              <li>
                <a href="/api/logout">Logout</a>
              </li>
            </>
          ) : (
            <>
              <li>
                <a href="/api/register">Register</a>
              </li>
              <li>
                <a href="/api/login">Login</a>
              </li>
            </>
          ))}
      </ul>
      <style jsx>{`
        ul {
          display: flex;
          list-style: none;
          margin-left: 0;
          padding-left: 0;
        }
        li {
          margin-right: 1rem;
        }
        li:nth-child(2) {
          margin-right: auto;
        }
        a {
          color: #000;
          text-decoration: none;
        }
        button {
          font-size: 1rem;
          color: #000;
          cursor: pointer;
          border: none;
          background: none;
        }
      `}</style>
    </Nav>
  );
}

Header.getInitialProps = async () => {};

export default Header;
