import fetch from "isomorphic-unfetch";
import styled from "styled-components";
import MeactMarkdown from "react-markdown";

import MainLayout from "../layouts/MainLayout";
import { getProposalMetadata, getProposalContent } from "../queries";
import ReactMarkdown from "react-markdown";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Containter = styled.div`
  max-width: 1000px;
`;

function Proposal({ metadata, content }) {
  console.log(metadata);
  return (
    <MainLayout>
      <Wrapper>
        <Containter>
          <h1>{metadata.title}</h1>
          <h2>{metadata.description}</h2>
          <ReactMarkdown source={content} escapeHtml={false} />
        </Containter>
      </Wrapper>
    </MainLayout>
  );
}

Proposal.getInitialProps = async function(context) {
  const metadata = await getProposalMetadata(1);
  const content = await getProposalContent(1);
  return { metadata, content };
};

export default Proposal;
