import fetch from "isomorphic-unfetch";
import styled from "styled-components";
import MeactMarkdown from "react-markdown";
import { useRouter } from "next/router";
import { withAuth, withLoginRequired } from "use-auth0-hooks";

import MainLayout from "../layouts/MainLayout";
import { ContentContainer } from "components";
import {
  getProposalBranches,
  getProposalMetadata,
  getProposalSummary,
  getProposalLegal,
  PROPOSAL_BY_SLUG_QUERY
} from "../queries";
import ReactMarkdown from "react-markdown";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Containter = styled.div`
  font-size: 18px;
  line-height: 1.4;
  max-width: 900px;
  padding: 60px 20px 0;
`;

function Proposal({ auth }) {
  const { loading, error, data } = useQuery(PROPOSAL_BY_SLUG_QUERY, {
    variables: { slug: "a-new-proposal" }
  });

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {JSON.stringify(error)}</p>;
  }

  const router = useRouter();

  const handleBranchSelect = e => {
    const selectedBranch = e.target.value;
    if (selectedBranch !== currentBranch) {
      if (selectedBranch !== "master") {
        router.push(`/hello/tree/${selectedBranch}`);
      } else {
        router.push("/hello");
      }
    }
  };

  return (
    <MainLayout>
      <ContentContainer>
        {/*
        <select
          onChange={e => handleBranchSelect(e)}
          defaultValue={currentBranch || "master"}
        >
          {branches.map(branch => (
            <option key={branch.name} value={branch.name}>
              {branch.name}
            </option>
          ))}
        </select>
          */}
        <h1>{data.proposal.title}</h1>
        <h2>{data.proposal.description}</h2>
        <ReactMarkdown source={data.proposal.summary} />
        <ReactMarkdown source={data.proposal.legal} />
      </ContentContainer>
    </MainLayout>
  );
}

export default withLoginRequired(withAuth(Proposal));
