import gql from "graphql-tag";

const PROPOSAL_BY_SLUG_QUERY = gql`
  query getProposalBySlug($slug String!) {
    proposal(slug: $slug) {
      id
      title
      description
      summary
      legal
    }
  }
`;

export default PROPOSAL_BY_SLUG_QUERY;
