import gql from "graphql-tag";

const CREATE_PROPOSAL_MUTATION = gql`
  mutation CreateProposal($title: String!) {
    createProposal(title: $title) {
      id
      title
      slug
    }
  }
`;

export default CREATE_PROPOSAL_MUTATION;
